  var meetingNumber = '89401084962'
  var role = 0
  var leaveUrl = 'https://Pusse-01.github.io/zoom_integration_react_app'
  var userName = 'React'
  var userEmail = ''
  var passWord = ''
  // pass in the registrant's token if your meeting or webinar requires registration. More info here:
